import React, { useEffect, useState } from 'react';
import { Link, Route } from 'react-router-dom';
import { callApi } from '../api';
import { Register } from './';
import Post from './Post';
import Posts from './Posts';

/*
Routes:
/ - home
/register - register
/login - login
/posts - all posts
    /posts/{id} - single post 
Plan:
- fetch all posts
- set up route for displaying all posts
- write a component to display posts
- set up route for displaying a single post
*/

const fetchUserData = async (token) => {
    const { data } = await callApi({
        url: '/users/me',
        token,
    });

    return data;
};

const fetchPosts = async () => {
    const {
        data: { posts },
    } = await callApi({
        url: '/posts',
    });

    return posts;
};

const App = () => {
    const [token, setToken] = useState('');
    const [userData, setUserData] = useState({});
    const [posts, setPosts] = useState([]);

    const isLoggedIn = userData.username !== undefined;

    const onLogOutClick = () => {
        localStorage.removeItem('st-token');
        setToken('');
        setUserData({});
    };

    useEffect(async () => {
        if (posts.length === 0) {
            const fetchedPosts = await fetchPosts();
            setPosts(fetchedPosts);
        }
    });

    useEffect(async () => {
        if (!token) {
            setToken(localStorage.getItem('st-token'));
            return;
        }
        const data = await fetchUserData(token);
        setUserData(data);
    }, [token]);

    return (
        <>
            <h1>Welcome! <br></br>Please sign in <a href="url">link text</a>or create an account.</h1>
            <Route exact path="/">
                {isLoggedIn ? (
                    <>
                        <div id ="Greeting">Hello, {userData.username}</div>
                        <button onClick={onLogOutClick}>Log Out</button>
                    </>
                ) : (
                    <>
                        <button>
                            <Link to="/register">Register</Link>
                        </button>
                        <button>
                            <Link to="/login">Login</Link>
                        </button>
                    </>
                )}
                <button>
                    <Link to="/posts">View All Posts</Link>
                </button>
            </Route>
            <Route exact path="/posts">
                <Posts posts={posts} />
            </Route>
            <Route path="/posts/:postId">
                <Post posts={posts} />
            </Route>
            <Route path="/register">
                <Register action="register" setToken={setToken} />
            </Route>
            <Route path="/login">
                <Register action="login" setToken={setToken} />
            </Route>
        </>
    );
};

export default App
