import React from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import { callApi } from '../api';
import MessageForm from './MessageForm';




const Post = ({posts, post, token}) => {
    const { postId } = useParams();
    const history = useHistory();

    if (posts.length === 0) return null;

    let postToRender;

    if (post) {
        postToRender = post;
    } else {
        postToRender = posts.find((post) => postId === post._id);
    }

        
        const onDelete = async (event) => {
            event.preventDefault();
    
    
            const data = await callApi({
                url: `/posts/${postToRender._id}`,
                method: 'DELETE',
                token,
            });
        
    
                history.push('/posts');
                window.location.reload()
        }
    return (
        
        <>
        
            <h2>{postToRender.title}</h2>
            <div>Submitted by: {postToRender.author.username}</div>
            <div>Description: {postToRender.description}</div>
            <div>Price: {postToRender.price}</div>
            <div>Location: {postToRender.location}</div>
            <div>Delivers: {postToRender.willDeliver ? 'Yes' : 'No'}</div>
                {/* <MessageForm postID = {posttoRender._id} token = {token}/> */}

            {!post ? <Link to="/posts">Back to all posts</Link> : null}
            {postToRender.isAuthor 
            ? 
            <>
            {postToRender.messages?.map (() => {

                <div>
                <div>Price: {postToRender.price}</div>
                <div>Location: {postToRender.location}</div></div>

            }

            )




            }

            <button>
            <Link to={"/EditPost/" + postToRender._id}>Edit Post</Link>
        </button>
            <button onClick={onDelete}>Delete Post</button> 
            </>
            : 
            <>
            <MessageForm postID = {postToRender._id} token = {token}/>
            </>
} 
  </>       
    );
};


export default Post;
